addon.presenter:
function AddonModel_Viewer_ODN_create() {

var presenter = function(){}
    presenter.wasInitiated = false;
    presenter.AnnotationsVisibility = "visible";
    presenter.isAREnabled = false;

    presenter.run = function(view, model){
        //console.log("ModelViewerODN run!");
        //console.log("model:", model);
        if(!presenter.wasInitiated) presenter.init(view, model);
    }

    presenter.createPreview = function(view, model){
        if(!presenter.wasInitiated) presenter.init(view, model);
    }

    presenter.init = function(view, model){
        //console.log("MV init");
        presenter.model = model;

        //https://github.com/bensonruan/webcam-easy
        class Webcam{constructor(e,t="user",s=null,i=null){this._webcamElement=e,this._webcamElement.width=this._webcamElement.width||1200,this._webcamElement.height=this._webcamElement.height||.75*this._webcamElement.width,this._facingMode=t,this._webcamList=[],this._streamList=[],this._selectedDeviceId="",this._canvasElement=s,this._snapSoundElement=i}get facingMode(){return this._facingMode}set facingMode(e){this._facingMode=e}get webcamList(){return this._webcamList}get webcamCount(){return this._webcamList.length}get selectedDeviceId(){return this._selectedDeviceId}getVideoInputs(e){return this._webcamList=[],e.forEach(e=>{"videoinput"===e.kind&&this._webcamList.push(e)}),1==this._webcamList.length&&(this._facingMode="user"),this._webcamList}getMediaConstraints(){var e={};return""==this._selectedDeviceId?e.facingMode=this._facingMode:e.deviceId={exact:this._selectedDeviceId},{video:e,audio:!1}}selectCamera(){for(let e of this._webcamList)if("user"==this._facingMode&&e.label.toLowerCase().includes("front")||"enviroment"==this._facingMode&&e.label.toLowerCase().includes("back")){this._selectedDeviceId=e.deviceId;break}}flip(){this._facingMode="user"==this._facingMode?"enviroment":"user",this._webcamElement.style.transform="",this.selectCamera()}async start(e=!0){return new Promise((t,s)=>{this.stop(),navigator.mediaDevices.getUserMedia(this.getMediaConstraints()).then(i=>{this._streamList.push(i),this.info().then(i=>{this.selectCamera(),e?this.stream().then(e=>{t(this._facingMode)}).catch(e=>{s(e)}):t(this._selectedDeviceId)}).catch(e=>{s(e)})}).catch(e=>{s(e)})})}async info(){return new Promise((e,t)=>{navigator.mediaDevices.enumerateDevices().then(t=>{this.getVideoInputs(t),e(this._webcamList)}).catch(e=>{t(e)})})}async stream(){return new Promise((e,t)=>{navigator.mediaDevices.getUserMedia(this.getMediaConstraints()).then(t=>{this._streamList.push(t),this._webcamElement.srcObject=t,"user"==this._facingMode&&(this._webcamElement.style.transform="scale(-1,1)"),this._webcamElement.play(),e(this._facingMode)}).catch(e=>{console.log(e),t(e)})})}stop(){this._streamList.forEach(e=>{e.getTracks().forEach(e=>{e.stop()})})}snap(){if(null!=this._canvasElement){null!=this._snapSoundElement&&this._snapSoundElement.play(),this._canvasElement.height=this._webcamElement.scrollHeight,this._canvasElement.width=this._webcamElement.scrollWidth;let e=this._canvasElement.getContext("2d");return"user"==this._facingMode&&(e.translate(this._canvasElement.width,0),e.scale(-1,1)),e.clearRect(0,0,this._canvasElement.width,this._canvasElement.height),e.drawImage(this._webcamElement,0,0,this._canvasElement.width,this._canvasElement.height),this._canvasElement.toDataURL("image/png")}throw"canvas element is missing"}}

        presenter.webcamElement = $(view).find('.md-webcam').get(0);
        presenter.webcam = new Webcam(presenter.webcamElement, 'user', '', '');

        presenter.model.EnvironmentImage = (presenter.model.EnvironmentImage == "" ? "neutral" : presenter.model.EnvironmentImage);

        //presenter.init(view, model);
        presenter.modelViewer = $(view).find("model-viewer").get(0);
        $(presenter.modelViewer).attr("src", presenter.model.Model);
        $(presenter.modelViewer).attr("poster", presenter.model.Poster);
        $(presenter.modelViewer).attr("skybox-image", presenter.model.SkyboxImage);
        $(presenter.modelViewer).attr("environment-image", presenter.model.EnvironmentImage);
        $(presenter.modelViewer).attr("shadow-intensity", presenter.model.ShadowIntensity);
        $(presenter.modelViewer).attr("shadow-softness", presenter.model.ShadowSoftness);
        $(presenter.modelViewer).attr("alt", presenter.model.AltText);
        //auto-rotate
        if(presenter.model.AutoRotate == "True") $(presenter.modelViewer).attr("auto-rotate", true);

        //handle annotations
        $(presenter.modelViewer).append(model.Annotations);
        presenter.model.LabelsEnabled == "True" ? presenter.showAnnotations() : presenter.hideAnnotations();

        //handle buttons
        presenter.arButton = $(view).find(".arButton").get(0);
        presenter.labelsButton = $(view).find(".labelsButton").get(0);
        presenter.copyButton = $(view).find(".copyButton").get(0);
        presenter.copyMessage = $(view).find(".copyMessage").get(0);

        $( presenter.arButton ).on( "click", function() {
            $(presenter.arButton).toggleClass("arButton-selected");
            $(presenter.arButton).hasClass("arButton-selected") ? presenter.startAR() : presenter.stopAR();            
        });

        if( presenter.model.Annotations.trim().length === 0 ) $( presenter.labelsButton ).css("display","none");
        presenter.model.LabelsEnabled == "True" ? $(presenter.labelsButton).addClass("labelsButton-selected") : $(presenter.labelsButton).removeClass("labelsButton-selected");
        $( presenter.labelsButton ).on( "click", function(e) {
            $(presenter.labelsButton).toggleClass("labelsButton-selected");
            $(presenter.labelsButton).hasClass("labelsButton-selected") ? presenter.showAnnotations() : presenter.hideAnnotations();            
        });

        //handle copyright
        let copyText = '<div class="copyContainer">'+presenter.model.CopyInfo+'</div>';
        //console.log("copyText",copyText)
        $(presenter.copyMessage).append(copyText);
        if( presenter.model.CopyInfo == "" ) $( presenter.copyButton ).css("display","none");

        //fix hyperlink clicking
        presenter.copyContainer = $(presenter.copyMessage).find(".copyContainer").get(0);
        let link = presenter.copyContainer.querySelector('a');
        if (link) {
            link.addEventListener('click', function(event) {
                event.preventDefault();
                window.open(link.getAttribute('href'), '_blank');
            });
        }

        $( presenter.copyButton ).on( "click", function(e) {
            $(presenter.copyButton).toggleClass("copyButton-selected");
            $(presenter.copyMessage).toggleClass("copyMessage-visible");
        });
        /* 03.02.2023 COPY INFO MOVED TO LAST PAGE - REMOVING BUTTON FROM PAGES
            30.04.2023 JEDNAK WYÅšWIETLAMY
         */
        //$( presenter.copyButton ).css("display","none");

        //set scale on model load
        $(presenter.modelViewer).on('load', function(){
            if(presenter.model.Scale !== undefined && presenter.model.Scale != "") presenter.setScale(presenter.model.Scale);
        });

        presenter.wasInitiated = true;
    }

    presenter.setScale = function(scale){
        presenter.modelViewer.scale = scale+" "+scale+" "+scale;
    }

    presenter.capture = function(video, scaleFactor) {
        if(scaleFactor == null){
            scaleFactor = 1;
        }
        var w = video.videoWidth * scaleFactor;
        var h = video.videoHeight * scaleFactor;
        var canvas = document.createElement('canvas');
        canvas.width  = w;
        canvas.height = h;
        var ctx = canvas.getContext('2d');
        ctx.drawImage(video, 0, 0, w, h);


        return canvas;
    }

    presenter.startAR = function(){
        presenter.isAREnabled = true;
        $(presenter.webcamElement).css("display","block");
        $(presenter.modelViewer).attr("shadow-intensity", 0);
        $(presenter.modelViewer).attr("skybox-image", "");

        presenter.webcam.start()
        .then(result =>{
            //console.log("webcam started");

            var img;
            presenter.ARInterval = setInterval(function(){
                img = presenter.capture(presenter.webcamElement).toDataURL('image/jpeg')
                $(presenter.modelViewer).attr("environment-image", img);
            },80)
        })
        .catch(err => {
            console.log(err);
        });
    }

    presenter.stopAR = function(){
        //console.log("Ar stopped")
        clearInterval(presenter.ARInterval);
        presenter.isAREnabled = false;

        presenter.webcam.stop();
        $(presenter.webcamElement).css("display","none");
        $(presenter.modelViewer).attr("shadow-intensity", presenter.model.ShadowIntensity);
        $(presenter.modelViewer).attr("skybox-image", presenter.model.SkyboxImage);
        setTimeout(function(){
            $(presenter.modelViewer).attr("environment-image", presenter.model.EnvironmentImage);
        },81);
    }

    presenter.showAnnotations = function(){
        presenter.setAnnotationsVisibility("visible");
        $(presenter.labelsButton).addClass("labelsButton-selected");
    }
    presenter.hideAnnotations = function(){
        presenter.setAnnotationsVisibility("hidden");
        $(presenter.labelsButton).removeClass("labelsButton-selected");
    }
    presenter.setAnnotationsVisibility = function(value) {
        $(presenter.modelViewer).find(".Hotspot").css("visibility", value);
        presenter.AnnotationsVisibility = value;
    }

    presenter.getAnnotationsVisibility = function(){
        //console.log("modelviewerodn presenter.AnnotationsVisibility: ", presenter.AnnotationsVisibility);
        return presenter.AnnotationsVisibility;
    }

    presenter.executeCommand = function(name, params) {
        var commands = {
            'startAR': presenter.startAR,
            'stopAR': presenter.stopAR,
            'showAnnotations': presenter.showAnnotations,
            'hideAnnotations': presenter.hideAnnotations,
            'getAnnotationsVisibility': presenter.getAnnotationsVisibility,
            'setScale': presenter.setScale
        };
        Commands.dispatch(commands, name, params, presenter);
    }; 

    presenter.setShowErrorsMode = function(){
        //element.innerHTML = 'setShowErrorsMode';
    }

    presenter.setWorkMode = function(){
        //element.innerHTML = 'setWorkMode';
    }

    presenter.reset = function(){
        //element.innerHTML = 'Reset';
    }

    presenter.getErrorCount = function(){
        //return 7;
    }

    presenter.getMaxScore = function(){
        //return 3;
    }

    presenter.getScore = function(){
        //return 1;
    }

    presenter.getState = function(){
        clearInterval(presenter.ARInterval);

        return JSON.stringify({
            AnnotationsVisibility: presenter.AnnotationsVisibility,
            isAREnabled: presenter.isAREnabled
        });
    };

    presenter.setState = function(stateString){
        if (ModelValidationUtils.isStringEmpty(stateString)) return;
        var state = JSON.parse(stateString);
        presenter.AnnotationsVisibility = state.AnnotationsVisibility;
        presenter.isAREnabled = state.isAREnabled;

        //console.log("presenter.AnnotationsVisibility",presenter.AnnotationsVisibility)
        //console.log("presenter.isAREnabled",presenter.isAREnabled)

        presenter.AnnotationsVisibility == "visible" ? presenter.showAnnotations() : presenter.hideAnnotations();

        if(presenter.isAREnabled){
            $(presenter.arButton).addClass("arButton-selected");
            presenter.startAR();
        }else{
            $(presenter.arButton).removeClass("arButton-selected");
            presenter.stopAR();
        }
    };

    return presenter;
}
